import * as React from "react"

import { cn } from "@/lib/utils"

const Card = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ class, ...props }, ref) => (
  <div
    ref={ref}
    class={cn(
      "rounded-xl border bg-card text-card-foreground shadow",
      class
    )}
    {...props}
  />
))
Card.displayName = "Card"

const CardHeader = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ class, ...props }, ref) => (
  <div
    ref={ref}
    class={cn("flex flex-col space-y-1.5 p-6", class)}
    {...props}
  />
))
CardHeader.displayName = "CardHeader"

const CardTitle = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLHeadingElement>
>(({ class, ...props }, ref) => (
  <h3
    ref={ref}
    class={cn("font-semibold leading-none tracking-tight", class)}
    {...props}
  />
))
CardTitle.displayName = "CardTitle"

const CardDescription = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ class, ...props }, ref) => (
  <p
    ref={ref}
    class={cn("text-sm text-muted-foreground", class)}
    {...props}
  />
))
CardDescription.displayName = "CardDescription"

const CardContent = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ class, ...props }, ref) => (
  <div ref={ref} class={cn("p-6 pt-0", class)} {...props} />
))
CardContent.displayName = "CardContent"

const CardFooter = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ class, ...props }, ref) => (
  <div
    ref={ref}
    class={cn("flex items-center p-6 pt-0", class)}
    {...props}
  />
))
CardFooter.displayName = "CardFooter"

export { Card, CardHeader, CardFooter, CardTitle, CardDescription, CardContent }
